import React from "react";
import Movie from "../component/Movie";
import MainVisual from "../component/MainVisual";
import PopularTV from "../component/PopularTV";
import PopularMovie from "../component/PopularMovie";

const Main = ({movieData,popularMovieData,popularTVData}) => {
  let filteredData = movieData.results.filter(movieData => movieData.overview !== '');
  let filterTVdData = popularTVData.results.filter(popularTVData => popularTVData.overview !== '');
  let filterMovieData = popularMovieData.results.filter(popularMovieData => popularMovieData.overview !== '');

  return(
    <div className="main">
      <MainVisual filteredData={filteredData}/>
      <h3>현재 상영중인 영화</h3>
      <Movie filteredData={filteredData}/>
      <h3>인기있는 영화</h3>
      <PopularMovie filterMovieData={filterMovieData}></PopularMovie>
      <h3>인기있는 TV 프로그램</h3>
      <PopularTV filterTVdData={filterTVdData}/>
    </div>
  )
}

export default Main;
